mapper.select:
SELECT m1.id, m1.pid, m1.img_url imgUrl, m1.default_img defaultImg, m1.price price, m1.simplename, m1.description, m1.card_id cardId, m1.user_id userId, m1.create_time createTime, m1.create_by createBy, m1.update_time updateTime, m1.update_by updateBy, m1.flag1, m1.flag2, IF ( DATE_SUB(CURDATE(), INTERVAL 3 DAY) <= date(m1.create_time), 1, 0 ) label, IF ( COUNT( m2.id )> 0, 1, 0 ) hasChildren FROM wx_material m1 LEFT JOIN wx_material m2 ON m2.pid =m1.id WHERE m1.is_deleted = 0 AND m1.card_id = #{cardId}
AND m1.pid = #{pid}
GROUP BY m1.id ORDER BY m1.id DESC
